-- ===============================================================
-- Fichero: populate.sql
-- Descripción: Pobla la base de datos bicicleta_data con 10 bicis y 500 filas de datos,
--              simulando rutas reales en distintos distritos de Bilbao.
-- ===============================================================

SET client_encoding TO 'UTF8';

DO $$
BEGIN
    IF NOT EXISTS (SELECT 1 FROM bikes LIMIT 1) THEN

        -- ===============================================================
        -- Inserción de datos de sensores (bike_data)
        -- Se generan 20 rutas (2 por bici) con datos de trayectoria:
        --   - Cada ruta tiene marcas temporales consecutivas a 1s de diferencia.
        --   - La posición inicia en un punto basado en el distrito y se va actualizando
        --     (con incrementos de 0.00005 en latitud y 0.00002 en longitud, salvo en algunos instantes donde se "para").
        
        -- En cada ruta la generación de coordenadas es la siguiente:
        --   - Punto inicial = (base_lat + 0.0001, base_lon + 0.0001)
        --   - Para cada segundo (i) de la ruta:
        --         si i mod 10 == 5 → se repite la posición anterior (simulando parada)
        --         en otro caso se suma delta: +0.00005 a la latitud y +0.00002 a la longitud.
        --   - La marca de tiempo se incrementa 1 segundo a cada fila.
        -- ===============================================================

        -- ************** BIKE 1 **************
        -- Ruta 1: Bike 1, Abando, 30 filas, inicio '2025-03-18 08:00:00'
        -- Base: (43.2630, -2.9350)  → punto inicial: (43.2631, -2.9349)

        -- Ruta 2: Bike 1, Basurto-Zorroza, 20 filas, inicio '2025-03-04 09:00:00'
        -- Base: (43.2530, -2.9380)  → punto inicial: (43.2531, -2.9379)

        -- ************** BIKE 2 **************
        -- Ruta 1: Bike 2, Begoña, 30 filas, inicio '2025-03-22 11:15:30'
        -- Base: (43.2850, -2.9490) → inicial: (43.2851, -2.9489)

        -- Ruta 2: Bike 2, Deusto, 20 filas, inicio '2025-04-10 16:42:15'
        -- Base: (43.2520, -2.9600) → inicial: (43.2521, -2.9599)

        -- ************** BIKE 3 **************
        -- Ruta 1: Bike 3, Ibaiondo, 30 filas, inicio '2025-03-28 14:23:45'
        -- Base: (43.2620, -2.9320) → inicial: (43.2621, -2.9319)

        -- Ruta 2: Bike 3, Otxarkoaga-Txurdinaga, 20 filas, inicio '2025-04-15 20:45:10' (noche)
        -- Base: (43.3000, -2.9700) → inicial: (43.3001, -2.9699)

        -- ************** BIKE 4 **************
        -- Ruta 1: Bike 4, Rekalde, 30 filas, inicio '2025-04-02 09:17:20'
        -- Base: (43.2650, -2.9200) → inicial: (43.2651, -2.9199)

        -- Ruta 2: Bike 4, Uribarri, 20 filas, inicio '2025-03-25 22:11:35' (noche)
        -- Base: (43.2550, -2.9300) → inicial: (43.2551, -2.9299)

        -- ************** BIKE 5 **************
        -- Ruta 1: Bike 5, Abando, 26 filas, inicio '2025-03-18 08:40:00'
        -- (Base Abando: (43.2630, -2.9350) → inicial: (43.2631, -2.9349)

        -- Ruta 2: Bike 5, Basurto-Zorroza, 20 filas, inicio '2025-04-04 14:23:15'
        -- Base: (43.2530, -2.9380) → inicial: (43.2531, -2.9379)

        -- ************** BIKE 6 **************
        -- Ruta 1: Bike 6, Begoña, 21 filas, inicio '2025-03-25 09:15:40'
        -- Base: (43.2850, -2.9490) → inicial: (43.2851, -2.9489)

        -- Ruta 2: Bike 6, Deusto, 20 filas, inicio '2025-04-10 16:45:30'
        -- Base: (43.2520, -2.9600) → inicial: (43.2521, -2.9599)

        -- ************** BIKE 7 **************
        -- Ruta 1: Bike 7, Ibaiondo, 26 filas, inicio '2025-03-22 17:52:15'
        -- Base: (43.2620, -2.9320) → inicial: (43.2621, -2.9319)

        -- Ruta 2: Bike 7, Otxarkoaga-Txurdinaga, 20 filas, inicio '2025-04-14 20:30:45'
        -- Base: (43.3000, -2.9700) → inicial: (43.3001, -2.9699)

        -- ************** BIKE 8 **************
        -- Ruta 1: Bike 8, Rekalde, 30 filas, inicio '2025-03-22 11:27:15'
        -- Base: (43.2650, -2.9200) → inicial: (43.2651, -2.9199)

        -- Ruta 2: Bike 8, Uribarri, 20 filas, inicio '2025-04-08 08:42:30'
        -- Base: (43.2550, -2.9300) → inicial: (43.2551, -2.9299)

        -- ************** BIKE 9 **************
        -- Ruta 1: Bike 9, Abando, 30 filas, inicio '2025-03-26 16:42:10'
        -- Base: (43.2630, -2.9350) → inicial: (43.2631, -2.9349)

        -- Ruta 2: Bike 9, Basurto-Zorroza, 20 filas, inicio '2025-04-02 13:15:25'
